void data::setup(){
    
    
    nFrames = 30;
    
    //----------------------------------------------------------------
    // the data is stored in a big flat array, let's copy it into something more managable
    // don't have to touch this :)
    
    for (int i = 0; i < 30; i++){
        for (int j = 0; j < 24; j++){
            float x = data3d[ i * 24 * 3  + j * 3 + 0 ];
            float y = data3d[ i * 24 * 3  + j * 3 + 1 ];
            float z = data3d[ i * 24 * 3  + j * 3 + 2 ];
            frames[i][j].set(x,y,z);
        }
    }
    
    for (int i = 0; i < 30; i++){
        for (int j = 0; j < 24; j++){
            float x = data2d[ i * 24 * 2  + j * 2 + 0 ];
            float y = data2d[ i * 24 * 2  + j * 2 + 1 ];
            frames2d[i][j].set(x,y);
        }
    }
    //----------------------------------------------------------------

}

vector < ofPoint >  data::get3dDataForFrame(int frame){
    vector < ofPoint > pts;
    while (frame < 0) frame += nFrames;
    while (frame >= nFrames) frame -= nFrames;
    for (int i = 0; i < 24; i++){
        pts.push_back( frames[frame][i]);
    }
    return pts;
    
}

vector < ofPoint >  data::get2dDataForFrame(int frame){
    vector < ofPoint > pts;
    while (frame < 0) frame += nFrames;
    while (frame >= nFrames) frame -= nFrames;
    for (int i = 0; i < 24; i++){
        pts.push_back( frames2d[frame][i]);
    }
    return pts;
}


